#!/bin/bash
# Test script for Docker infrastructure

set -e

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

echo -e "${BLUE}🧪 Testing ActivityWatch Docker Infrastructure${NC}"
echo ""

# Check if Docker is available
if ! command -v docker &> /dev/null; then
    echo -e "${RED}✗ Docker is not installed${NC}"
    exit 1
fi

if ! docker info > /dev/null 2>&1; then
    echo -e "${RED}✗ Docker daemon is not running${NC}"
    echo "Please start Docker Desktop or the Docker daemon"
    exit 1
fi

# Check if docker-compose is available
if command -v docker-compose &> /dev/null; then
    COMPOSE_CMD="docker-compose"
elif docker compose version &> /dev/null; then
    COMPOSE_CMD="docker compose"
else
    echo -e "${RED}✗ docker-compose is not available${NC}"
    exit 1
fi

cd "$(dirname "$0")/.."

echo -e "${BLUE}📋 Step 1: Validating configuration...${NC}"
if ! $COMPOSE_CMD config > /dev/null 2>&1; then
    echo -e "${RED}✗ docker-compose.yml has errors${NC}"
    $COMPOSE_CMD config
    exit 1
fi
echo -e "${GREEN}✓ Configuration is valid${NC}"
echo ""

echo -e "${BLUE}📋 Step 2: Stopping any existing containers...${NC}"
$COMPOSE_CMD down -v 2>/dev/null || true
echo -e "${GREEN}✓ Cleaned up${NC}"
echo ""

echo -e "${BLUE}📋 Step 3: Starting database and Redis...${NC}"
$COMPOSE_CMD up -d db redis
echo "Waiting for services to be healthy..."
sleep 5

# Wait for database to be ready
echo "Waiting for database..."
for i in {1..30}; do
    if $COMPOSE_CMD exec -T db pg_isready -U aw_user -d activitywatch > /dev/null 2>&1; then
        echo -e "${GREEN}✓ Database is ready${NC}"
        break
    fi
    if [ $i -eq 30 ]; then
        echo -e "${RED}✗ Database failed to start${NC}"
        $COMPOSE_CMD logs db | tail -20
        exit 1
    fi
    sleep 1
done

# Wait for Redis to be ready
echo "Waiting for Redis..."
for i in {1..30}; do
    if $COMPOSE_CMD exec -T redis redis-cli -a redis_password ping > /dev/null 2>&1; then
        echo -e "${GREEN}✓ Redis is ready${NC}"
        break
    fi
    if [ $i -eq 30 ]; then
        echo -e "${RED}✗ Redis failed to start${NC}"
        $COMPOSE_CMD logs redis | tail -20
        exit 1
    fi
    sleep 1
done
echo ""

echo -e "${BLUE}📋 Step 4: Testing database connection and schema...${NC}"
# Test database connection
if $COMPOSE_CMD exec -T db psql -U aw_user -d activitywatch -c "SELECT 1;" > /dev/null 2>&1; then
    echo -e "${GREEN}✓ Database connection successful${NC}"
else
    echo -e "${RED}✗ Database connection failed${NC}"
    exit 1
fi

# Check if TimescaleDB extension is installed
if $COMPOSE_CMD exec -T db psql -U aw_user -d activitywatch -c "SELECT * FROM pg_extension WHERE extname = 'timescaledb';" | grep -q timescaledb; then
    echo -e "${GREEN}✓ TimescaleDB extension installed${NC}"
else
    echo -e "${YELLOW}⚠ TimescaleDB extension not found${NC}"
fi

# Check if tables exist
if $COMPOSE_CMD exec -T db psql -U aw_user -d activitywatch -c "\dt" | grep -q buckets; then
    echo -e "${GREEN}✓ Database tables created${NC}"
else
    echo -e "${YELLOW}⚠ Database tables not found (may need initialization)${NC}"
fi
echo ""

echo -e "${BLUE}📋 Step 5: Testing Redis connectivity...${NC}"
if $COMPOSE_CMD exec -T redis redis-cli -a redis_password ping | grep -q PONG; then
    echo -e "${GREEN}✓ Redis connection successful${NC}"
    
    # Test Redis operations
    $COMPOSE_CMD exec -T redis redis-cli -a redis_password SET test_key "test_value" > /dev/null 2>&1
    if $COMPOSE_CMD exec -T redis redis-cli -a redis_password GET test_key | grep -q test_value; then
        echo -e "${GREEN}✓ Redis read/write operations working${NC}"
        $COMPOSE_CMD exec -T redis redis-cli -a redis_password DEL test_key > /dev/null 2>&1
    fi
else
    echo -e "${RED}✗ Redis connection failed${NC}"
    exit 1
fi
echo ""

echo -e "${BLUE}📋 Step 6: Building API service...${NC}"
echo "This may take several minutes..."
if $COMPOSE_CMD build api 2>&1 | tee /tmp/aw-api-build.log | grep -q "Successfully built\|error\|Error"; then
    if grep -q "error\|Error" /tmp/aw-api-build.log; then
        echo -e "${RED}✗ API build failed${NC}"
        tail -50 /tmp/aw-api-build.log
        exit 1
    else
        echo -e "${GREEN}✓ API build successful${NC}"
    fi
else
    echo -e "${YELLOW}⚠ Build output unclear, checking logs...${NC}"
    tail -50 /tmp/aw-api-build.log
fi
echo ""

echo -e "${BLUE}📋 Step 7: Starting API service...${NC}"
$COMPOSE_CMD up -d api
echo "Waiting for API to be healthy..."
sleep 10

# Wait for API health check
for i in {1..60}; do
    if $COMPOSE_CMD exec -T api wget --quiet --tries=1 --spider http://localhost:8080/health > /dev/null 2>&1; then
        echo -e "${GREEN}✓ API is healthy${NC}"
        break
    fi
    if [ $i -eq 60 ]; then
        echo -e "${RED}✗ API failed to become healthy${NC}"
        echo "API logs:"
        $COMPOSE_CMD logs api | tail -30
        exit 1
    fi
    sleep 1
done
echo ""

echo -e "${BLUE}📋 Step 8: Testing API endpoints...${NC}"
# Test health endpoint
if curl -s http://localhost:8080/health 2>/dev/null | grep -q "healthy\|ok"; then
    echo -e "${GREEN}✓ Health endpoint responding${NC}"
else
    echo -e "${YELLOW}⚠ Health endpoint check unclear${NC}"
fi

# Test info endpoint (if available)
if curl -s http://localhost:8080/api/0/info 2>/dev/null | grep -q "version\|name"; then
    echo -e "${GREEN}✓ API info endpoint responding${NC}"
else
    echo -e "${YELLOW}⚠ API info endpoint may not be available${NC}"
fi
echo ""

echo -e "${BLUE}📋 Step 9: Checking service status...${NC}"
$COMPOSE_CMD ps
echo ""

